import { useEffect, useState } from "react";
import { getAllScore } from "../../api";
import styles from "./Leaderboard.module.css";
import { formatTime } from "../../helpers";

export function Leaderboard() {
  const [leaders, setLeaders] = useState([]);

  useEffect(() => {
    // Вызываем функцию getAllScore() при монтировании компонента
    getAllScore()
      .then(data => {
        // Устанавливаем полученные данные в состояние
        setLeaders(data.leaders); // Обратите внимание на использование data.leaders
      })
      .catch(error => {
        // Обработка ошибок
        console.error(error);
      });
  }, []);

  return (
    <div className={styles.leaderboardContainer}>
      <div className={`${styles.leaderboard} ${styles.leaderboardTitle}`}>
        <p className={styles.item}>Позиция</p>
        <p className={styles.itemCenter}>Пользователь</p>
        <p className={styles.item}>Время</p>
      </div>
      <>
        {leaders
          .slice() // Создаем копию массива, чтобы избежать изменения оригинального массива
          .sort((a, b) => a.time - b.time) // Сортируем по возрастанию времени
          .map((leader, index) => (
            <div className={styles.leaderboard} key={index}>
              <p className={styles.item}>{`# ${leader.id}`}</p>
              <p className={styles.itemCenter}>{leader.name}</p>
              <p className={styles.item}>{formatTime(leader.time)}</p>
            </div>
          ))}
      </>
    </div>
  );
}
