export function getAllScore() {
  return fetch("https://wedev-api.sky.pro/api/leaderboard").then(response => {
    if (!response.ok) {
      throw new Error("Произошла ошибка сервера, попробуйте позже");
    }
    return response.json();
  });
}

export function postScore({ email, password }) {
  return fetch("https://skypro-music-api.skyeng.tech/user/signup/", {
    method: "POST",
    body: JSON.stringify({
      email: email,
      password: password,
      username: email,
    }),
    headers: {
      "content-type": "application/json",
    },
  });
}
