import { Link } from "react-router-dom";
import styles from "./SelectLevelPage.module.css";
import { useState } from "react";

export function SelectLevelPage() {
  //Чек-бокс облегченной версии игры вкл/выкл
  const [isEasyLevel, setEasyLevel] = useState(false);

  // Обработчик изменения состояния чекбокса
  const handleCheckboxChange = () => {
    setEasyLevel(easyLevel => !easyLevel);
  };

  return (
    <div className={styles.container}>
      <div className={styles.modal}>
        <h1 className={styles.title}>Выбери сложность</h1>
        <ul className={styles.levels}>
          <li className={styles.level}>
            <Link className={styles.levelLink} to="/game/3">
              1
            </Link>
          </li>
          <li className={styles.level}>
            <Link className={styles.levelLink} to="/game/6">
              2
            </Link>
          </li>
          <li className={styles.level}>
            <Link className={styles.levelLink} to="/game/9">
              3
            </Link>
          </li>
        </ul>
        <label className={styles.checkboxContainer}>
          <input type="checkbox" name="checkbox-attempt" checked={isEasyLevel} onChange={handleCheckboxChange} />
          <span className={styles.checkboxCustom}></span>
          Поражение при трёх ошибках
        </label>
      </div>
    </div>
  );
}
