import { useState } from "react";
import { Tooltip } from "react-tooltip";
import styles from "./Superpowers.module.css";

export function AlohomoraPower() {
  const [tooltipVisible, setTooltipVisible] = useState(false);

  const handleTooltipVisibility = () => {
    setTooltipVisible(true);
    setTimeout(() => {
      setTooltipVisible(false);
    }, 2000); // скрыть подсказку через 2 секунды
  };

  return (
    <>
      <div className={styles.superpowerContainer}>
        <img
          className={styles.superpowerIcon}
          src="../power-alohomora.svg"
          alt="Суперсила алохомора"
          onMouseEnter={handleTooltipVisibility}
          onMouseLeave={() => setTooltipVisible(false)}
        />
      </div>
      {tooltipVisible && (
        <Tooltip id="alohomoraTooltip" place="top" type="dark" effect="float">
          <span>Суперсила "Алохомора": открывается случайная пара карт.</span>
        </Tooltip>
      )}
    </>
  );
}
