//суперсила "Прозрение" - на 5 секунд показываются все карты. Таймер длительности игры на это время останавливается.
import { useState } from "react";
import styles from "./Superpowers.module.css";

export function PiphanyPower({ hint }) {
  const [showHintPiphany, setShowHintPiphany] = useState(false);

  return (
    <div
      className={styles.superpowerContainer}
      onMouseEnter={() => {
        setShowHintPiphany(true); // Показываем подсказку при наведении
        onMouseEnter(); // Вызываем функцию, переданную через пропс, если нужно
      }}
      onMouseLeave={() => {
        setShowHintPiphany(false); // Скрываем подсказку при уводе курсора
        onMouseLeave(); // Вызываем функцию, переданную через пропс, если нужно
      }}
    >
      {showHintPiphany && <div className={styles.hint}>{hint}</div>}{" "}
      {/* Показываем подсказку, если состояние showHintPiphany равно true */}
      <img className={styles.superpowerIcon} src="../power-piphany.svg" alt="Суперсила прозрение" />
    </div>
  );
}
