//суперсила "Прозрение" - на 5 секунд показываются все карты. Таймер длительности игры на это время останавливается.
import { useState } from "react";
import styles from "./Superpowers.module.css";

export function PiphanyPower({ onMouseEnter, onMouseLeave, hint }) {
  const [showHint, setShowHint] = useState(false);

  return (
    <div
      className={styles.superpowerContainer}
      onMouseEnter={() => {
        setShowHint(true);
        onMouseEnter(); // Вот здесь вызываем функцию, переданную через пропс
      }}
      onMouseLeave={() => {
        setShowHint(false);
        onMouseLeave();
      }}
    >
      {showHint && <div className={styles.hint}>{hint}</div>}
      <img className={styles.superpowerIcon} src="../power-piphany.svg" alt="Суперсила прозрение" />
    </div>
  );
}
