import { useEffect, useState } from "react";
import { getAllScore } from "../../api";
import styles from "./Leaderboard.module.css";

export function Leaderboard() {
  const [leaders, setLeaders] = useState([]);

  useEffect(() => {
    // Вызываем функцию getAllScore() при монтировании компонента
    getAllScore()
      .then(data => {
        // Устанавливаем полученные данные в состояние
        setLeaders(data.leaders); // Обратите внимание на использование data.leaders
      })
      .catch(error => {
        // Обработка ошибок
        console.error(error);
      });
  }, []);

  return (
    <div className={styles.leaderboardContainer}>
      <div className={`${styles.leaderboard} ${styles.leaderboardTitle}`}>
        <p>Позиция</p>
        <p>Пользователь</p>
        <p>Время</p>
      </div>
      <div className={styles.leaderboard}>
        {leaders.map(
          (
            leader,
            index, // Мапим каждого лидера
          ) => (
            <div key={index}>
              <p>{leader.id}</p>
              <p>{leader.name}</p>
              <p>{leader.time}</p>
            </div>
          ),
        )}
      </div>
    </div>
  );
}
