//суперсила "Алохомора" - открывается случайная пара карт
import { useState } from "react";
import styles from "./Superpowers.module.css";

export function AlohomoraPower({ hint }) {
  const [showHintAlohomora, setShowHintAlohomora] = useState(false);

  return (
    <div
      className={styles.superpowerContainer}
      onMouseEnter={() => {
        setShowHintAlohomora(true); // Показываем подсказку при наведении
        onMouseEnter(); // Вызываем функцию, переданную через пропс, если нужно
      }}
      onMouseLeave={() => {
        setShowHintAlohomora(false); // Скрываем подсказку при уводе курсора
        onMouseLeave(); // Вызываем функцию, переданную через пропс, если нужно
      }}
    >
      {showHintAlohomora && <div className={styles.hint}>{hint}</div>}{" "}
      {/* Показываем подсказку, если состояние showHintAlohomora равно true */}
      <img className={styles.superpowerIcon} src="../power-alohomora.svg" alt="Суперсила алохомора" />
    </div>
  );
}
