import { Link, useNavigate } from "react-router-dom";
import styles from "./SelectLevelPage.module.css";
import { useDispatch, useSelector } from "react-redux";
import { setEasyMode } from "../../store/slices";
import { Button } from "../../components/Button/Button";
import { useState } from "react";

export function SelectLevelPage() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Статус режима игры до трех ошибок
  const isEasyMode = useSelector(store => store.game.setEasyMode);

  // Обработчик изменения состояния чекбокса для установки режима игры до трех ошибок
  const handleCheckboxChange = event => {
    dispatch(setEasyMode(event.target.checked));
  };

  //старт игры в зависимости от выбраанного уровня
  const [choosenLevel, setChoosenLevel] = useState(null);

  function handleStartButton() {
    navigate(`/game/${choosenLevel}`);
  }

  return (
    <div className={styles.container}>
      <div className={styles.modal}>
        <h1 className={styles.title}>Выбери сложность</h1>
        <ul className={styles.levels}>
          <li className={styles.level}>
            <Link
              className={choosenLevel === 3 ? styles.choosenLevel : styles.levelLink}
              onClick={() => setChoosenLevel(3)}
            >
              1
            </Link>
          </li>
          <li className={styles.level}>
            <Link
              className={`${styles.levelLink} ${choosenLevel === 6 ? styles.choosenLevel : styles.levelLink}`}
              onClick={() => setChoosenLevel(6)}
            >
              2
            </Link>
          </li>
          <li className={styles.level}>
            <Link
              className={`${styles.levelLink} ${choosenLevel === 9 ? styles.choosenLevel : styles.levelLink}`}
              onClick={() => setChoosenLevel(9)}
            >
              3
            </Link>
          </li>
        </ul>
        <label className={styles.checkboxContainer}>
          <input type="checkbox" name="checkbox-attempt" checked={isEasyMode} onChange={handleCheckboxChange} />
          <span className={styles.checkboxCustom}></span>
          Легкий режим (3 жизни)
        </label>
        <Button children={"Играть"} onClick={handleStartButton} />
        <Link className={styles.leaderboardLink} to="/leaderboard">
          Перейти к лидерборду
        </Link>
      </div>
    </div>
  );
}
