import React, { useState } from "react";
import { Tooltip } from "react-tooltip";
import styles from "./Superpowers.module.css";

export function AlohomoraPower() {
  const [tooltipVisible, setTooltipVisible] = useState(false);

  const handleMouseEnter = () => {
    setTooltipVisible(true);
  };

  const handleMouseLeave = () => {
    setTooltipVisible(false);
  };

  return (
    <div className={styles.superpowerContainer} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <Tooltip
        id="alohomoraTooltip"
        place="top"
        type="dark"
        effect="float"
        getContent={() => 'Суперсила "Алохомора": открывается случайная пара карт.'}
        eventOff="mouseleave"
        globalEventOff="mouseleave"
        event="mouseover"
        overridePosition={({ left, top }) => {
          return { top: top + 10, left };
        }}
        clickable
        isOpen={tooltipVisible}
      >
        <img className={styles.superpowerIcon} src="../power-alohomora.svg" alt="Суперсила алохомора" />
      </Tooltip>
    </div>
  );
}
