export function getAllScore() {
  return fetch("https://wedev-api.sky.pro/api/leaderboard").then(response => {
    if (!response.ok) {
      throw new Error("Произошла ошибка сервера, попробуйте позже");
    }
    return response.json();
  });
}

export function postScore({ name, time }) {
  return fetch("https://wedev-api.sky.pro/api/leaderboard", {
    method: "POST",
    body: JSON.stringify({
      name: name,
      time: time,
    }),
    headers: {
      "content-type": "application/json",
    },
  });
}
