//суперсила "Прозрение" - на 5 секунд показываются все карты. Таймер длительности игры на это время останавливается.
import { showTooltip } from "../../utils/tooltip";
import styles from "./Superpowers.module.css";

export function PiphanyPower() {
  const handleMouseEnter = () => {
    const powerInfo = document.querySelector(".superpowerIcon");
    const tooltipContent = `
      <div class="return-info__tooltip-content">
        <p>Если товары вам не подойдут, мы вернем их обратно на склад — это бесплатно</p>
      </div>`;
    showTooltip(powerInfo, tooltipContent);
  };

  return (
    <div className={styles.superpowerContainer}>
      <img
        className={styles.superpowerIcon}
        src="../power-piphany.svg"
        alt="Суперсила прозрение"
        onMouseEnter={handleMouseEnter}
      />
    </div>
  );
}
