import { Link } from "react-router-dom";
import styles from "./SelectLevelPage.module.css";
import { useDispatch, useSelector } from "react-redux";
import { setEasyMode } from "../../store/slices";
import { useState } from "react";

export function SelectLevelPage() {
  // Статус режима игры до трех ошибок
  const isEasyMode = useSelector(store => store.game.setEasyMode);

  const dispatch = useDispatch();

  // Обработчик изменения состояния чекбокса для установки режима игры до трех ошибок
  const handleCheckboxChange = event => {
    dispatch(setEasyMode(event.target.checked));
  };

  const [selectedLevel, setSelectedLevel] = useState(null);
  const history = useHistory();

  const handlePlay = () => {
    if (selectedLevel === 1) {
      history.push("/game/3");
    } else if (selectedLevel === 2) {
      history.push("/game/6");
    } else if (selectedLevel === 3) {
      history.push("/game/9");
    } else {
      const elements = document.querySelectorAll(".level");
      elements.forEach(element => {
        element.classList.add(styles.levelNotSelected);
      });
    }
  };

  ///game/6

  return (
    <div className={styles.container}>
      <div className={styles.modal}>
        <h1 className={styles.title}>Выбери сложность</h1>
        <ul className={styles.levels}>
          <li className={styles.level} onClick={() => setSelectedLevel(1)}>
            1
          </li>
          <li className={styles.level} onClick={() => setSelectedLevel(2)}>
            2
          </li>
          <li className={styles.level} onClick={() => setSelectedLevel(3)}>
            3
          </li>
        </ul>
        <label className={styles.checkboxContainer}>
          <input type="checkbox" name="checkbox-attempt" checked={isEasyMode} onChange={handleCheckboxChange} />
          <span className={styles.checkboxCustom}></span>
          Легкий режим (3 жизни)
        </label>
        <Link className={styles.levelLink} onClick={handlePlay}>
          Играть
        </Link>
      </div>
    </div>
  );
}
